CREATE TABLE BOOKS (ISBN VARCHAR(255) NOT NULL, AUTHOR VARCHAR(255), PRICE FLOAT, SUBJECT VARCHAR(255), TITLE VARCHAR(255), PRIMARY KEY (ISBN))
