CREATE TABLE ODETAILS (PRICE INTEGER, QTY INTEGER, ONO INTEGER NOT NULL, ISBN VARCHAR(255) NOT NULL, PRIMARY KEY (ONO, ISBN))
CREATE TABLE MEMBERS (USERID VARCHAR(255) NOT NULL, ADDRESS VARCHAR(255), CITY VARCHAR(255), CREDITCARDNUMBER VARCHAR(255), CREDITCARDTYPE VARCHAR(255), EMAIL VARCHAR(255), FNAME VARCHAR(255), LNAME VARCHAR(255), PASSWORD VARCHAR(255), PHONE VARCHAR(255), STATE VARCHAR(255), ZIP INTEGER, PRIMARY KEY (USERID))
CREATE TABLE ORDERS (ONO INTEGER NOT NULL, RECEIVED DATE, SHIPADDRESS VARCHAR(255), SHIPCITY VARCHAR(255), SHIPPED DATE, SHIPSTATE VARCHAR(255), SHIPZIP INTEGER, USERID VARCHAR(255), PRIMARY KEY (ONO))
